(()=>{class e{value=0;progressPie=document.getElementById("progress-pie");constructor(){this.init()}init(){const e=document.getElementById("value-input");e&&e.addEventListener("change",(e=>{this.progressPie&&(this.progressPie.style.backgroundImage=`conic-gradient(\n                        gray ${3.6*e.target.value}deg,\n                        lightblue 0 ${360-3.6*e.target.value}deg\n                        )`)}));const t=document.getElementById("animated-input");t&&t.addEventListener("change",(()=>{this.progressPie.classList.toggle("animated")}));const n=document.getElementById("hide-input");n&&n.addEventListener("change",(e=>{this.progressPie.style.display=e.target.checked?"none":"flex"}))}}window.onload=()=>{new e}})();